import React from "react";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import todolist from "./images/todolist.png";
import weatherapp from "./images/weatherapp.png";
import cryptoapp from "./images/cryptoapp.png";
import quizapp from "./images/weatherapp.png";
import github from "./images/github3.png";
import link from "./images/link.png";

const Projects = () => {
  const projects = [
    {
      image: quizapp,
      title: "Quiz App",
      description:
        "This is a simplified version of a quiz game that allows players to test their knowledge in various categories and difficulty levels. The application provides an interactive and engaging quiz experience, where players can select a category and difficulty level before starting the game.      ",
      techStack: " HTML, CSS, JavaScript, React",
      livePreviewLink: "https://quizz-andreeea.netlify.app/ ",
      viewCodeLink: "https://github.com/AndreeaCsecs/quiz-app",
    },
    {
      image: todolist,
      title: "To-do app",
      description:
        "A simple to-do app built using React that allows users to create and manage a list of tasks with features such as not started, in progress, and completed tasks, week day choice. The app is integrated with third-party libraries like React-Bootstrap to provide a modern and visually appealing interface.",
      techStack: " HTML, CSS, JavaScript, React, Bootstrap",
      livePreviewLink: "https://to-do-andreea.netlify.app/",
      viewCodeLink: "https://github.com/AndreeaCsecs/to-do-app",
    },
    {
      image: weatherapp,
      title: "Weather App",
      description:
        "A simple weather app built using React would allow users to input a location and receive current weather data for that location, including temperature, humidity, and wind speed. The app uses HTTPS Requests to fetch data from api.weatherapi.com and integrates with third-party libraries like React-Bootstrap, to provide a clean and user-friendly interface.      ",
      techStack: " HTML, CSS, JavaScript, React, Bootstrap, HTTPS Requests",
      livePreviewLink: "https://weather-andreea.netlify.app/",
      viewCodeLink: "https://github.com/AndreeaCsecs/weather-app",
    },
    {
      image: cryptoapp,
      title: "Crypto App",
      description:
        "A simple crypto app built using React and Bootstrap that displays the latest prices and daily changes of various cryptocurrencies along with their market cap. The app uses HTTP requests to fetch data from APIs such as Binance API to retrieve real-time data and provide a user-friendly interface.      ",
      techStack: " HTML, CSS, JavaScript, React, Bootstrap, HTTP Requests",
      livePreviewLink: "https://crypto-andreea.netlify.app/",
      viewCodeLink: "https://github.com/AndreeaCsecs/crypto-app",
    },
  ];

  return (
    <div className="Projects text-center container my-5 py-3" id="projects">
      <div className="m-2">
        <p className="fw-bold mb-3" id="titles">
          Projects
        </p>
        <p className="p-3" id="subtitle">
          Things I've built so far
        </p>
      </div>

      <Carousel
        showArrows={true}
        showThumbs={false}
        showStatus={false}
        swipeable={true}
        draggable={true}
        infiniteLoop={true}
        infinite={false}
        autoPlay={true}
        showDots={false}
        interval={4000}
        transitionTime={500}
        centerMode={true}
      >
        {projects.map((project, index) => (
          <div key={index} className="mx-3">
            <div class="card">
              <a
                href={project.livePreviewLink}
                className="text-dark"
                target="_blank"
                rel="noreferrer"
              >
                <div className="image-wrapper">
                  <img src={project.image} alt={project.title} />
                </div>
              </a>
              <div class="card-body">
                <p className="card-text text-muted mt-3" id="app-description">
                  {project.description}
                </p>
                <p className="text-start m-2 fw-bold" id="tech-stack">
                  Tech stack: {project.techStack}
                </p>
                {/*<a href="#" class="btn btn-primary">
                  Go somewhere
                </a>*/}
                <div type="button" className="btn btn-light github m-2 ">
                  <a
                    href={project.viewCodeLink}
                    className="text-dark"
                    target="_blank"
                    rel="noreferrer"
                  >
                    <div className="d-inline-flex p-2 justify-content space-around">
                      <img
                        src={github}
                        alt=""
                        className="link-icons"
                        style={{ marginRight: "8px" }}
                      />
                      <span>Github</span>
                    </div>
                  </a>
                </div>
              </div>
            </div>

            {/*
            <div className="legend d-flex justify-content-around">
              <p className="app-titles">
                <b>{project.title}</b>
              </p>
              <p
                className="card-text text-start text-muted my-3 mx-2"
                id="app-description"
              >
                {project.description}
              </p>
              <p className="text-start m-2 fw-bold" id="tech-stack">
                Tech stack: {project.techStack}
              </p>
              <ul className="list-inline m-3">
                <li className="list-inline-item mx-3">
                  <img src={link} alt="" className="link-icons" />
                  <a
                    href={project.livePreviewLink}
                    className="text-dark"
                    target="_blank"
                    rel="noreferrer"
                  >
                    Live Preview
                  </a>
                </li>
                <li className="list-inline-item mx-3">
                  <img src={github} alt="" className="link-icons" />
                  <a
                    href={project.viewCodeLink}
                    className="text-dark"
                    target="_blank"
                    rel="noreferrer"
                  >
                    View Code
                  </a>
                </li>
              </ul>
            </div>
            */}
          </div>
        ))}
      </Carousel>
    </div>
  );
};

export default Projects;
